SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les objectifs du cours seront&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;1- de connaitre le devenir des medicaments dans l'organisme.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;nous verrons que 4 phases sont individualisees a savoir l'absorption, la distribution,  la metabolisation et l'elimination.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pour chacune de ces phases, nous aborderons les notions de bases de pharmacocinetique quantitative cad que nous verrons les principaux parametres pharmacocinetiques qui permettent des les quantifier&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;enfin, nous etudierons pourquoi les individus ne repondent pas tous de la meme facon a un traitement, en abordant les principales sources de variabilite de reponse aux medicaments.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
