SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;pour vous presenter la majorite des notions de pharmacocinetique, j'aborderai essentiellement la voie digestive, car:&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;c'est une voie indirecte, cad celle ou les parametres sont les plus variables, importants,&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;c'est une voie frequente d'utilisation donc d'interet d'autant plus general,&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'administration d'un medicament par voie digestive necessite donc une premiere etape qui est celle de la liberation et de la dissolution du principe actif puis son passage dans la circulation sanguine &lt;/span&gt;&lt;span  fontweight='bold'&gt;cad sa resorption&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une petite remarque : le terme d'absorption, bien que le plus utilise peut etre ambigue en ce sens qu'absorber veut aussi dire avaler.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la pharmacocinetique s'interesse donc a la resorption.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;comme j' ai essaye de le representer sur cette diapositive, la resorption necessite le franchissement par le principe actif de la barriere digestive cad le franchissement  de l'epithelium digestif et le franchissement de l'endothelium vasculaire. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les proprietes physico chimiques des principes actifs vont donc fortement influencer leur resorption.  &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@absorption des medicaments@";
SDpagedocid[SDpagenum] = SDdocnum;
