SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide5.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;un premier mecanisme d'absorption des medicaments est leur diffusion passive a travers les phospholipides, qui representent 80 % de la surface des membrane cellulaires.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ce mecanisme de diffusion passive est le principal mecanisme implique dans l'absorption.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la diffusion passive s'effectue selon un gradient de concentration du milieu ou la molecule est la plus concentree vers le milieu ou la molecule est la moins concentree.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en consequences, en tant que phenomene passif, elle ne consomme pas d'energie et  n'est pas saturable.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@absorption des medicaments@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide6.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la bicouche lipidique membranaire (representee en bleu sur cette diapositive) est permeable aux substances qui sont liposolubles&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;donc la diffusion passive concernera surtout les molecules apolaires&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en revanche, la bicouche lipidique des membranes cellulaires est impermeable aux ions na+, k+, cl-et aux formes moleculaires ionisees.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;en consequences, seules les formes moleculaires des medicaments diffuseront a travers la bicouche lipidique&lt;/span&gt;&lt;span &gt;. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cela implique que le ph du milieu jouera un role important dans l'absorption des medicaments par diffusion passive.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un medicament acide faible sera sous forme moleculaire a ph acide et sera donc plus facilement resorbe en milieu acide (par exemple au niveau de l'estomac). en revanche, ill sera sous forme ionisee en milieu basique et ne sera que peu resorbee en milieu basique (par exemple au niveau de l'intestin grele).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@absortion par diffusion passive@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide7.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'absorption ou la resorption des medicaments, et de facon plus generale, le passage des medicaments a travers les barrieres biologiques par mecanisme passif peut se faire selon differentes modalites &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;par passage transcellulaire : lorsque les cellules sont serrees les unes contre les autres, le medicament doit traverser les cellules elles memes et donc traverser&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;par passage paracellulaire: lorsque les cellules sont separees les une des autres par des jonctions plus laches, les medicaments peuvent passer par ces jonctions appelees gap jonction (representes tres schematiquement en vert sur cette diapositive) &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@diffusion passive@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide8.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le transfert passif d'un compose se fait selon un gradient de concentration qui est determine par la loi de fick ou le le flux correspondant a la diffusion passive (cas le nombre de molecule qui diffusent a travers la membrane par unite de temps) est determine par l'equation suivante &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;c1 = est la concentration la plus elevee de part et d'autre de la membrane&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;c2 la concentration la plus faible de part et d'autre de la membrane&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la surface = la surface de la membrane a travers laquelle la diffusion a lieu&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le coefficient de permeabilite = la mesure de la mobilite de la molecule dans le milieu de diffusion&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'epaisseur de la membrane = la longueur du trajet&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@franchissement des barrieres@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide9.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;apres le passage intercellulaire et le transport passif, &lt;/span&gt;&lt;span  fontweight='bold'&gt;l'absorption par transport actif est&lt;/span&gt;&lt;span &gt; le troisieme mecanisme de transfert transmembranaire.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il necessite l'existence de transporteurs proteiques, generalement specifiques d'un compose endogene (acides aminees ou sucres) ou alimentaire, mais qui est de taille trop importante ou trop insoluble dans les lipides pour pouvoir diffuser passivement a travers les membranes. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ce systeme de transport est le seul systeme de transport transmembranaire qui peut fonctionner contre un gradient de concentration et il necessite donc de l'energie. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ce transporteur peut etre emprunte par un xenobiotique possedant une certaines analogie structurale avec le ligand naturel.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;du fait du nombre restreint de proteines de transport, ce processus est saturable, et peut donner lieu a des phenomenes de competition entre deux medicaments ou entre un medicament et un compose endogene.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;de nombreuses cellules de l'organisme contiennent aussi des transporteurs de membranes qui sont specialises pour expulser les xenobiotiques, par exemple la p-glycoproteine ou pgp est un transporteur responsable de la resistance a certains traitements.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@resorption digestive par mecanisme actif :  role des transporteurs enterocytaires@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide10.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les transporteurs membranaires fonctionnent generalement de maniere coordonnee, pour permettre le passage d'un medicament d'un pole a l'autre de la cellule.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ils sont localises soit au niveau de la membrane basolaterale permettant l'extraction du medicament present dans le sang et son entree dans la cellule, soit au niveau apical, permettant la sortie du medicament de la cellule vers la lumiere.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ils ont principalement localises au niveau des enterocytes, et joueront donc un role dans l'absorption digestive, mais on en trouve aussi au niveau de la barriere hematoencephalique, et des tubules renaux, et auront donc respectivement un role dans la distribution et l'elimination des medicaments.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on distingue 2 grandes familles de transporteurs : les transporteurs de la familles slc pour solute carrier et les transporteurs de la famille abc (ou atp-binding cassettes)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;-les transporteurs de la famille slc qui permettent l'entree des substances dans la cellules : on les appelle aussi les transporteurs d'efflux. les plus importants en pharmacologie sont les aot ou organic anion transporteurs et les oct ou organic cation transporters.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;-les transporteurs de la famille abc fonctionnent comme des pompes d'efflux, favorisant l'extrusion des medicaments et toxiques hors de la cellule. les plus importants en pharmacologie sont la glycoproteine p et les multidrug resistance related protein ou mrp.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;je ne vous demande de retenir que l'existence des oct, oat, pgp et mrp&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

