SDpagedisplay[SDpagenum] = "Slide13.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;si je prends, l'exemple de l'estomac, c'est un organe qui presente une surface d'echange de 1 m2, son ph est acide, son debit sanguin de perfusion est faible, et le temps de contact des aliments ou des pa est faible.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'absorption des pa sera donc mineure et concernera surtout les molecules acides qui seront donc sous forme moleculaire au niveau de l'estomac.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous comprenez ainsi aisement que toutes modification du ph gastrique, telle que les modifications de ph induites par certains medicament anti-ulcereux qui diminuent le ph gastrique, peuvent entrainer une diminution de la resorption gastrique de certains medicaments.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
