SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;si je prends maintenant l'exemple de l'intestin grele, sa surface d'echange est tres importante,  200 a 300 m2, so ph est plutot alcalin, le debit sanguin de perfusion est eleve 1 l/m.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous comprendrez aisement que l'absorption des medicaments, qui sont pour une majorite des bases faibles,  se fait pour une tres large part au niveau de l'intestin grele. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
