SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;si l'on administre par voie intraveineuse une dose de medicament, &lt;/span&gt;&lt;span  fontweight='bold'&gt;la totalite de la dose va se retrouver&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span  fontweight='bold'&gt;dans la circulation generale&lt;/span&gt;&lt;span  fontstyle='italic'&gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous avez represente, sur ce graphe, l'evolution des concentrations sanguins ou plasmatiques du medicament en fonction du temps apres une administration unique par voie iv.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;par definition, on considere donc que la biodisponibilite d'un medicament administre par par voie iv est de 100 %&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontstyle='italic'&gt;on distingue donc &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;-la biodisponibilite absolue (f en %) qui correspond au rapport de la quantite en pa absorbee par une voie d'administration extravasculaire&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la biodisponibilte  &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ex medicament dont la biodisponibilite est de 50 % : si on administre 1 g, 500 mg seront dans la circulation generale&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

