SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide18.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la biodisponibilite relative, permet quand a elle de comparer entre elles 2 formes du medicament administrees par la meme voie : exemple : comparer la biodisponibilite d'un medicmanent administre sous forme de gelules ou sous forme de comprimes., comprimes et gelules etant administres par la meme voie, le voie orale. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;dans notre exemple, la biodisponibilite relative sera donc definie comme etant le rapport de l'auc obtenue apres l'administration sous forme de gelule/l'auc obtenue apres administration sous forme de cps., le tout multiplie par 100 pour exprimer le rapport sous forme de porucentage.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la demonstration que ces parametres sont comparables permet de definir la bioequivallence des deux formes, qui sera un critere essentiel pour la reconnaissance d'un medicament generique&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

