SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;ce facteur vitesse d'absorption est apprecie par &lt;/span&gt;&lt;span  fontweight='bold'&gt;le temps&lt;/span&gt;&lt;span &gt; (que l'on appellera tmax) necessaire pour atteindre la concentration plasmatique ou sanguine de medicament maximale (ou cmax)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;vous avez ici represente, sur ces 2 graphes, l'evolution en fonction du temps des concentrations de 2 medicaments, le medicament a, et le medicament b.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
