SDpagedisplay[SDpagenum] = "Slide27.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;pour diffuser dans les tissus de l'organisme, le medicament doit a nouveau franchir a des &amp;quot;barrieres&amp;quot; biologiques.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;nous retrouverons les memes modes de de franchissement des barrieres biologiques, a savoir la diffusion intercellulaire, la diffusion passive et le transport actif. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;notons que la diffusion est un phenomene reversible cad qu'elle peut s 'effectuer du sang vers les tissus et des tissus vers le sang pour son elimination&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@facteurs influencant la distribution des medicaments@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide28.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;comme pour la phase de resorption, un certain nombre de facteurs influencent la distribution des medicaments, tel que leur poids moleculaire, leur proprietes physico chimiques , telles que la lipophilie qui peut favoriser la distribution des medicaments dans les graisses et le systeme nerveux central;la presence de transporteurs qui vont faciliter ou limiter l'entree des medicaments ou des toxiques dans les cellules. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;a titre d'exemple, on peut citer l'existence de tres nombreux transporteurs au niveau de la barriere hematoencephalique. leur presence au niveau de la barriere hematoencephalique jouen un role important dans la protection du systeme nerveux central, mais peut egalement nuire a la bonne efficacite des medicaments en limitant leur diffusion au niveau intra-cerebral&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

