SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide29.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;un autre facteur important qui influence la distribution des medicaments est la liaison aux proteines plasmatiques, albumine essentiellement, et alpha glycoproteine).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette liaison aux proteines plasmatiques obeit a la loi d'action de masse.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il s'instaure donc un etat d'equilibre entre la concentration de medicament libre, la concentration de proteines plasmatiques et la concentration de medicaments liee aux proteines plasmatiques.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ce phenomene de liaison est &lt;/span&gt;&lt;span  fontweight='bold'&gt;saturable&lt;/span&gt;&lt;span &gt;, et il peut y donner lieu a un phenomene de competition entre 2 medicaments ou entre  un medicament et une substance endogene, egalement liee aux proteines plasmatiques, telles que la bilirubine.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
