// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@metabolisme@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide41.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le metabolisme est, par definition, la conversion chimique irreversible d'une espece chimique en une autre.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on parle de metabolisme, ou metabolisation ou biotransformation.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la finalite de la phase de metabolisation est la detoxification de l'organisme , cad la diminution de l'activite ou l'elimination des composes que l'organisme considere comme toxiques.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il existe cependant une exception importante : le cas des pro-medicaments encore appeles pro-drogues qui sont des medicaments inactifs dans leur forme initiale, et qui necessitent une etape de biotransformation pour devenir actifs.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@biotransformation des medicaments@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide42.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;on peut ainsi resumer l'influence de la phase de biotransformation sur le devenir du medicament :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;-un medicament actif peut etre biotransforme en metabolite, lui aussi actif&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;-un medicament actif peut etre biotransforme en un metabolite inactif, c'est souvent le cas des medicaments a effet de premier passage hepatique important&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;-un medicament inactif que l'on appelle aussi pro-medicament, peut etre biotransforme en un metabolite actif.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@le foie est le lieu principal mais non exclusif de la biotransformation des medicaments@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide43.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le foie est le principal organe implique dans la biotransformation des medicaments.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cependant, ce n'est pas l'organe exclusif de la biotransformation des medicaments puisqu'elle s'effectue aussi au niveau de la barriere hemato-encephalique, au niveau pulmonaire, au niveau renal ou au niveau des enterocytes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@biotransformation des medicaments@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide44.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;comme je vous l'ai indique dans une diapositive precedente, la fonction primaire de la  biotransformation est une fonction de detoxification de l'organisme.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'organisme va donc tout mette en jeu pour eliminer le xenobiotique et donc le modifier sur le plan chimique. cela consistera a transformer les molecules apolaires liposolubles  en molecules polaires hydrosolubles, de facon a favoriser leur elimination. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;deux types de reactions chimiques peuvent intervenir : &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les reactions de phase 1 ou reaction de fonctionnalisation&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;-les reactions de phase 2 ou reaction de conjugaison.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;je vais maintenant vous detailler ces 2 types de reactions. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@les reactions de phase 1@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide45.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la phase 1 comporte des reactions d'hydroxylation dont le mecanisme reactionnel implique une oxydation.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;elles comportent des reactions d'hydroxylation, r pouvant etre aliphatique ou aromatique, et c un carbone,&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;-des reactions de n-oxydation ou de s-oxydation&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;-des reactions de n ou o-dealkylation&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@les enzymes de la phase 1@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide46.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;ces reactions d'oxydation sont essentiellement realisees par des enzymes appartenant a la superfamille des cytochrome p450.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le cytochrome p-450 constitue, en fait, non pas une enzyme unique mais une famille d'iso-enzymes a fer, metabolisant preferentiellement tel ou tel type de medicaments. les changements du degre d'oxydoreduction du fer sont a l'origine des biotransformations catalysees par l'enzyme. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il existe un grand nombre d'iso-enzymes du cytp450, classees en familles designees par un chiffre : 1, 2 ,3&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;chaque famille pouvant se subdivise en sous groupes designes par les lettres, a, b …&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;chaque famille metabolise des substrats determines, &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il existe un grand nombre d'iso-enzymes du cytochrome p450 ou cyp, classees en familles designees par les chiffres 1, 2, 3, chaque famille pouvant se subdiviser en sous-groupes designes par les lettres a, b etc. chaque famille metabolise preferentiellement des substrats determines, certains d'entre eux etant inducteurs de l'iso-enzyme correspondante. certaines substances sont des inhibiteurs. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cyp 1a2 qui metabolise, par exemple, la cafeine, la theophylline, la clozapine, l'imipramine, la tacrine. il est induit par le tabac. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cyp 2c9 qui metabolise la phenytoine, le tolbutamide, l'ibuprofene, la warfarine. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cyp 2i9 qui metabolise, l'omeprazole, le moclobemide, le diazepam, l'imipramine. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cyp 2d6 qui metabolise divers antidepresseurs, divers neuroleptiques, divers b-bloquants. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cyp 3a, notamment le 3a4 qui metabolise la clozapine, la terfenadine, le cisapride, l'erythromycine, la ciclosporine, la nifedipine. ainsi que le cortisol, la progesterone, la testosterone. le ketoconazole, l'erythromycine sont des inhibiteurs des cyp 3a. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cyp 2e1 qui metabolise les petites molecules dont les anesthesiques volatils. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on constate que le meme medicament peut etre metabolise par deux ou plusieurs iso-enzymes differentes. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@reactions de conjugaison (phase 2)@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide47.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la phase 2 comporte des reactions de conjugaison, soit par l'acide glucuronique (glucuroconjugaison), la glycine (glycoconjugaison), soit par le sulfate (sulfoconjugaison), ou encore l'acetate (n acetyl transferases).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la glucuroconjugaison constitue le mecanisme principal. elle est catalysee par des udp-glucuronyl transferase qui favorisent la fixation de l'acide gllucuronique sur un atome d'oxygene ou un atome de souffre de la molecule&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@les enzymes de la phase 2@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide48.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la glucuroconjugaison constitue le mecanisme principal. elle est catalysee par des udp-glucuronyl transferase qui favorisent la fixation de l'acide gllucuronique sur un atome d'oxygene ou un atome de souffre de la molecule&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@exemple@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide49.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;un medicament peut etre metabolise par diverses enzymes . les metabolites peuvent etre actifs ou inactifs&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la morphine est un exemple de molecules qui est biotransformee par de nombreuses voies&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la morphine est metabolisee au niveau du foie selon 2 types de reactions&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;-reaction d'oxydation :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;morphine n-oxyde&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;n-normorphine&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;-reaction de conjugaison&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;morphine 3-glucuronide (54 a 74 %, peu actif)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;morphine 6-grucuronide (&amp;lt;1 % mais puissant agoniste µ&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;morphine 3-sulfate (7.5 a  12 %)&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
