// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@pourquoi connaitre la biotransformation des medicaments ?@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide50.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;pourquoi est-il important de connaitre la biotransformation des medicaments ?&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il est important de connaitre la biotransformation des medicaments d'une part afin de prevenir certaines interactions medicamenteuses, et d'autre part,  afin de comprendre &lt;/span&gt;&lt;span  fontweight='bold'&gt;voire d'anticiper&lt;/span&gt;&lt;span &gt; la variabilite de la reponse a un traitement. et nous allons voire qu'il existe une susceptibilite genetique a la reponse a certains medicaments.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@interactions medicamenteuses@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide51.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;concernant les interactions medicamenteuses, certains principes actifs sont capables d'induire la synthese des cytochrome p450, cad qu'ils vont agir sur les genes impliques dans la synthese des cytochromes p450.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;a l'inverse, d'autres principes actifs vont inhiber l'activite des cytochromes p450.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@inhibition de la biotransformation par les cytochromes p450@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 2;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide52.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'inhibition des cytochromes p450 peut se faire selon 2 mecanismes :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une inhibition reelle cad que le medicament va se fixer sur le site catalytique du cytochrome p450 et l'inhiber&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ou, une  competition entre 2 medicaments administres simultanement et fixees et metabolisees par le m^me cytochrome/ ce deuxieme mecanisme, plus frequemment implique, resulte d'une affinite differente des 2 molecules administrees en meme temps. dans ces conditions, certains medicaments peuvent se comporter comme des inhibiteurs du metabolisme de substance moins affine, mais avoir egalement leur metabolisme inhibe par d'autres medicaments moins affines&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@interactions medicamenteuses et cytochromes p450@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide53.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;sur le plan biologique et cliniques,&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'inhibition des cytochromes p450 par un medicament aura pour consequences :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;-une diminution de l'activite enzymatique des cytochromes p450 et donc du metabolisme du medicament associe&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;-une augmentation de la concentration du medicament qui est associe, ce qui peut avoir pour consequences un renforcement de l'effet therapeutique ou l'apparition d'une toxicite&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;a l'inverse, l'induction des cytochromes p450 par un medicament inducteur entraine une augmentation de l'activite enzymatique et donc du metabolisme, ce qui aura en general pour consequence  une diminution de la concentration en medicament qui sera associe et donc soit une&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;perte d'efficacite therapeutique, qui necessite souvent une augmentation de la posologie du medicament associe&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en revanche, si le metabolite genere est toxique, l'association d'un medicament inducteur enzymatique peut etre a l'origine d'une toxiite.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@pourquoi connaitre la biotransformation des medicaments ?@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide54.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;comme je vous l'ai indique en preambule, il est egalement important de connaitre la biotransformation des medicaments car il existe une variabilite genetique interindividuelle de leur metabolisme.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@notion de polymorphisme genetique@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide55.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;en effet, il existe ce que l'on appelle un polymorphisme genetique pour certains genes codant pour des enzymes impliquees dans la biotransformation des medicaments. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il existe ainsi dans la population generale differents versions alleliques d'un meme gene. sa frequence doit etre &amp;gt;1 % dans la population generale pour que l'on parle de polymorphisme genetique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@polymorphismes genetiques et risques associes@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide56.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les polymorphismes rencontres, peuvent correspondre a une duplication du nombre de copies du gene codant pour l'enzyme, une deletion, ou une mutation du gene codant pour l'enzyme impliquee dans la biotransformation.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ces polymorphismes genetiques vont avoir des consequences fonctionnelles&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ainsi une duplication du gene codant pour le cytochrome, va avoir pour consequences une augmentation de l'expression des cytochrome p450 et donc une augmentation de l'activite enzymatique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette augmentation de l'activite enzymatique sera associee a une augmentation de la metabolisation avec sur le plan therapeutique, un risque d'inefficacite ou un risque de toxicite si le metabolite genere est toxique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la deletion d'un gene codant pour peut avoir pour consequences l'absence d'expression de l'enzyme, ce qui sera associe a une non metabolisation du medicament.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le medicament n'etant pas metabolise; il s'accumulera dans l'organisme et expose le patient a une risque de toxicite.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;-une mutation se traduira par une diminution de l'activite enzymatique, et donc une metabolisation reduite, ce qui expose a nouveau le patient a un risque d'accumulation de principe actif et donc a un risque de toxicite.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@ex polymophisme genetique du cytp450 2d6@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide57.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;une illustration des consequences cliniques de l'existence de polymorphismes genetiques sur la reponse cliniqiue a un traitement est &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la biotransformation de la codeine en morphine;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la codeine est un opiace biotransforme en morphione par le cytochrome p450 2d6.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;chez des patients metaboliseurs ultra rapides, cad presentant une augmentation du nombre de copies du gene codant pour le cytp450 2d6, la codeine est beaucoup plus metabolisee en morphine,que dans la population « sauvage » ce qui entraine l'apparition d'effets indesirables typiques de la morphine, comme la sedation et des douleurs abdominales importantes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@principaux polymorphismes genetiques impliques dans les  reactions de phase 1 de biotransformation des medicaments@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide58.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;de nombreuses isoformes des cytochromes p450 font l'objet d'un polymorphisme genetiques et ont donc des consequences cliniques importantes s sur un grand nombre de medicaments.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les enzymes intervenant dans les reactions de phases 1 ne sont pas les seules a faire l'objet de polymophismes genetiques, en effet des transferases intervenant dans les reactions de phase 2 peuvent aussi faire l'objet de polymorphismes genetiques&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@aspects pharmacocinetiques :   l'elimination@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide59.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'etape finale du devenir du medicament est son elimination de l'organisme;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ce processus d'elimination intervient apres la phase de metabolisation.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il peut concerner le medicament sous forme inchange », le metabolite inactif, le metabolite actif, le metabolite toxicque, un metabolite conjugue, un medicament conjugue.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
SDpageauhtor[SDpagenum] = "";

// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@objectifs du cours@";
SDpagedocid[SDpagenum] = SDdocnum;
