// Page Data
SDpagenum++;
SDpagetitle[SDpagenum] = "@place des reins et du foie dans le processus d'excretion@";
SDpagedocid[SDpagenum] = SDdocnum;
SDpageduration[SDpagenum] = 1;
SDpageaudio[SDpagenum] = "";
SDpagevideo[SDpagenum] = "";
SDpagedisplay[SDpagenum] = "Slide68.swf";
SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;comme je vous l'ai indique en introduction, la metabolisation et l'elimination ont une fonction de detoxification de l'organisme.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le foie permet essentiellement une transformation chimique du principe actif destine a le rendre plus polaire et donc plus hydrosoluble, le rein quant a lui ayant pour mission essentiellement d'eliminer ou d 'ecxcreter les molecules hydrophiles.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
