SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;comme son taux de production et son excretion sont constants d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;un jour a l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;autre, la mesure de la concentration plasmatique de creatinine peut en elle-meme suffire comme premiere estimation du dfg. voici les valeurs de creatininemie considerees comme normales chez l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;homme et la femme.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
