SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;cette courbe montre egalement tres bien la relation inverse entre le debit de filtration glomerulaire et la creatininemie.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la bonne correlation entre les deux parametres fait que le dosage de la creatinine plasmatique constitue le mode d'evaluation le plus repandu de la fonction renale.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;avec un petit bemol toutefois car, contrairement a l'inuline, la creatininemie n&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;est pas uniquement le reflet de l'excretion renale par la filtration glomerulaire mais egalement de l'absorption digestive et du metabolisme de la creatinine. elle peut donc etre augmentee en cas de consommation importante de viande ou d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;exercice intense.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
