SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;on ne peut pas bien sur mesurer directement le volume de chacun de ces compartiments liquidiens. il est toutefois possible de les estimer indirectement en mesurant la dilution d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;une quantite connue d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;un marqueur qui se distribue uniquement, uniformement et entierement dans le compartiment que l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;on veut mesurer.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le volume de distribution du marqueur dans le compartiment, donc le volume du compartiment lui-meme, est egal au rapport de la quantite de marqueur qui a ete administree sur la concentration de marqueur mesuree une fois l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;equilibre atteint.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le marqueur ideal pour ce genre de technique doit posseder les proprietes suivantes :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il doit se distribuer de facon homogene dans tout le compartiment&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il ne doit pas passer dans un autre compartiment&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il ne doit pas etre synthetise ou metabolise par l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;organisme et finalement&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;il ne doit pas presenter de toxicite. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
