SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;contrairement aux liquides extracellulaires, le liquide intracellulaire ne contient presque pas de sodium et de chlore. son cation le plus abondant est le &lt;/span&gt;&lt;span  fontweight='bold'&gt;potassium&lt;/span&gt;&lt;span &gt; et son principal anion est l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;ion phosphate &lt;/span&gt;&lt;span  fontweight='bold'&gt;hpo4(--). &lt;/span&gt;&lt;span &gt;les cellules contiennent aussi du magnesium et surtout des quantites importantes de proteines, environ trois fois plus que dans le plasma. l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;osmolarite du liquide intracellulaire resulte donc en grande partie des sels de potassium. elle est voisine de celle du lec a cause de l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;equilibre osmotique a travers la membrane cellulaire mais ici encore la forte concentration de proteines intracellulaires osmotiquement actives fait que l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;osmolarite du lic est legerement plus elevee que celle du lec. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
