SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;nous allons maintenant regarder les differents echanges d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;eau et de solutes entre les compartiments liquidiens de l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;organisme.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les trois principaux endroits ou se produisent ces echanges sont :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;- entre les compartiments extracellulaire et intracellulaire a travers la membrane cellulaire, &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;- entre les compartiments plasmatique et interstitiel du liquide extracellulaire a travers la paroi capillaire et finalement&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;entre le compartiment plasmatique et l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;environnement exterieur.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les deux premiers types d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;echange sont impliques dans l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;equilibre interne et le dernier dans l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;equilibre externe des substances de l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;organisme.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
