SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;en ce qui concerne les echanges d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;eau, a l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;exception de certaines cellules du tubule renal, les membranes cellulaires sont tres permeables a l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;eau ce qui permet son libre mouvement d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;un compartiment a l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;autre&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;deux forces vont determiner les mouvements d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;eau entre les compartiments liquidiens :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;osmose et &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la pression hydrostatique generee par le systeme cardiovasculaire&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
