SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;voici une simulation du fonctionnement d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;une aquaporine creee par une equipe de l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;universite de l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;illinois. il est estime que pres d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;un milliard de molecules d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;eau (les spheres rouges et blanches) passent par seconde par le canal forme par une aquaporine. la sphere jaune nous montre le trajet effectue par une seule molecule d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;eau. la simulation a revele que la selectivite du passage de l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;eau est apportee par une rotation de la molecule d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;eau a mi-parcours que vous pourrez voir avec un peu de patience.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
