SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;comme le plasma est en contact direct avec le milieu exterieur au niveau du tube digestif et des reins, il est possible de perdre ou de gagner de l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;eau ou des solutes et de modifier ainsi la composition des liquides corporels. le liquide extracellulaire est donc toujours le premier a subir des changements de volume et d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;osmolarite et ceux-ci vont ensuite se transmettre au liquide intracellulaire. nous allons dans les prochaines diapositives examiner diverses situations qui affectent le volume ou l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;osmolarite du lec et regarder l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;impact de ces modifications sur la distribution de l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;eau entre les compartiments extra et intracellulaire.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
