SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;afin de simplifier les choses nous allons dans les exemples qui vont suivre laisser de cote le prototype classique de l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;homme adulte de 70 kg pour utiliser les valeurs moyennes mesurees chez une femme de 60 kg car ceci nous donne des chiffres ronds avec une proportion corporelle d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;eau de &lt;/span&gt;&lt;span  fontweight='bold'&gt;50% &lt;/span&gt;&lt;span &gt;soit &lt;/span&gt;&lt;span  fontweight='bold'&gt;30 litres &lt;/span&gt;&lt;span &gt;repartis en &lt;/span&gt;&lt;span  fontweight='bold'&gt;10 litres de liquide extracellulaire &lt;/span&gt;&lt;span &gt;et &lt;/span&gt;&lt;span  fontweight='bold'&gt;20 litres de liquide intracellulaire&lt;/span&gt;&lt;span &gt;. a l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;equilibre, l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;osmolarite des compartiments est identique soit environ 300 mosmoles par litre et nous avons le meme nombre d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;osmoles par litre dans les liquides extra et intracellulaire c'est-a-dire 3000 et 6000 osmoles respectivement.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
