SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;un gain de liquide iso-osmotique&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;clic&lt;/span&gt;&lt;span &gt; : comme ici l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;infusion ou l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;ingestion de trois litres d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;une solution saline, va affecter uniquement le volume du liquide extracellulaire qui va augmenter de 3 litres sans changer celui du liquide intracellulaire qui restera stable a 20 litres. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;comme l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;apport de liquide est iso-osmotique,&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;clic : il n&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;y aura pas de mouvements d&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;eau entre les compartiments &lt;/span&gt;&lt;span &gt;et l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;osmolarite de ceux-ci ne changera pas. nous qualifierons cette situation &lt;/span&gt;&lt;span  fontweight='bold'&gt;d&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;expansion iso-osmotique du compartiment extracellulaire&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le liquide en exces va se repartir dans tout le compartiment extracellulaire et se distribuera entre les compartiments plasmatiques et interstitiels proportionnellement a la dimension de chaque compartiment. ainsi, comme le compartiment plasmatique represente le quart du liquide extracellulaire, il recevra le quart soit 750 ml des trois litres infuses ou ingeres.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
