SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;a l'inverse, une perte de 3 litres de liquide iso-osmotique&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;clic&lt;/span&gt;&lt;span &gt; : comme lors d'une une diarrhee severe ou encore d'une hemorragie, a les effets inverses. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;clic&lt;/span&gt;&lt;span &gt; : c&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;est-a-dire une diminution du volume du liquide extracellulaire (toujours avec la proportion un quart, trois quart entre les compartiments plasmatiques et interstitiel) sans changement du volume du liquide intracellulaire ni de l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;osmolarite des differents compartiments. il n&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;y a toujours pas de mouvements osmotiques d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;eau entre les compartiments. nous sommes ici dans une situation de &lt;/span&gt;&lt;span  fontweight='bold'&gt;contraction iso-osmotique du compartiment extracellulaire&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
