SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;nous allons maintenant aborder les echanges a l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;interieur du compartiment extracellulairec'est-a-dire ceux qui se produisent &lt;/span&gt;&lt;span  fontweight='bold'&gt;de part et d&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;autre de la paroi capillaire entre le plasma et le liquide interstitiel&lt;/span&gt;&lt;span &gt;. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la paroi capillaire est essentiellement formee d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;une seule couche de cellules endotheliales. les substances la traversent en passant soit directement a travers les cellules endotheliales soit entre les cellules.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les substances nutritives comme le glucose et les dechets metaboliques, de meme que l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;oxygene et le dioxide de carbone, vont quant a elles, s&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;echanger principalement par &lt;/span&gt;&lt;span  fontweight='bold'&gt;diffusion&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les echanges de liquide ont un impact moins important, ils servent surtout a equilibrer les volumes liquidiens des deux compartiments. ils vont s&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;effectuer essentiellement par &lt;/span&gt;&lt;span  fontweight='bold'&gt;filtration&lt;/span&gt;&lt;span &gt; sous l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;effet des differences de pressions de part et d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;autre de la paroi capillaire.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les deux pressions qui vont determiner les echanges liquidiens sont la &lt;/span&gt;&lt;span  fontweight='bold'&gt;pression osmotique &lt;/span&gt;&lt;span &gt;et la &lt;/span&gt;&lt;span  fontweight='bold'&gt;pression hydrostatique &lt;/span&gt;&lt;span &gt;du sang contre la paroi vasculaire. ces pressions sont parfois appelees&lt;/span&gt;&lt;span  fontweight='bold'&gt; forces de starling&lt;/span&gt;&lt;span &gt; du nom du physiologiste anglais qui les a decrites en premier.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
