SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;il en resulte une &lt;/span&gt;&lt;span  fontweight='bold'&gt;perte nette de liquide&lt;/span&gt;&lt;span &gt; dans le liquide interstitiel d'environ 2 ml par minute et donc de &lt;/span&gt;&lt;span  fontweight='bold'&gt;presque 3 litres par jour&lt;/span&gt;&lt;span &gt;. ce liquide est recupere par les &lt;/span&gt;&lt;span  fontweight='bold'&gt;capillaires lymphatiques&lt;/span&gt;&lt;span &gt; et retourne au compartiment plasmatique au niveau de la circulation veineuse. a l'equilibre, le drainage lymphatique permet donc de maintenir inchanges les volumes des compartiments plasmatique et interstitiel. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
