SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;comme nous l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;avons vu lors de l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;expansion et la contraction du volume extracellulaire, les echanges continus entre les compartiments plasmatique et interstitiel permettent de maintenir constants le volume plasmatique (et donc le volume sanguin) malgre le gain ou la perte de liquide isotonique par le compartiment plasmatique. cette fonction de &lt;/span&gt;&lt;span  fontweight='bold'&gt;tampon&lt;/span&gt;&lt;span &gt; du compartiment interstitiel permet de prevenir une hausse ou une chute trop rapide de volume plasmatique et ainsi de maintenir une &lt;/span&gt;&lt;span  fontweight='bold'&gt;circulation sanguine normale&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
