SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;comme vous le voyez sur cette diapositive, nous allons utiliser, pour les cours de physiologie renale, la meme terminologie que celle qui vous a ete proposee dans le cours d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;anatomie renale. si vous n&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;avez pas encore ecoute le cours du professeur chaffanjon, je vous conseille de le faire des a present (ou du moins d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;ecouter les huit premieres diapositives) car je ne reviendrai pas sur ces diverses structures.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
