SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;pour ce qui est de la nomenclature de la circulation renale, nous utiliserons les memes termes sauf en ce qui concerne la microcirculation du nephron. en effet, nous utiliserons ici le terme &lt;/span&gt;&lt;span  fontweight='bold'&gt;d&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;arteriole (et non d'artere), &lt;/span&gt;&lt;span &gt;comme&lt;/span&gt;&lt;span  fontweight='bold'&gt; &lt;/span&gt;&lt;span &gt;dans le cas des &lt;/span&gt;&lt;span  fontweight='bold'&gt;arterioles afferentes &lt;/span&gt;&lt;span &gt;que nous voyons ici, pour designer les petits vaisseaux qui amenent le sang au nephron a partir des arteres interlobulaires.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
