SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;chaque nephron est donc forme d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;un corpuscule renal associe a un tubule renal. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;clic : le corpuscule renal se compose d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;une sphere invaginee, la capsule de bowman, qui enveloppe un reseau de capillaires appele glomerule. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;en physiologie, nous divisons le tubule renal en quatre parties qui se distinguent du point de vue fonctionnel et histologique.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;clic : d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;abord le tubule contourne proximal qui emerge de la capsule de bowman et chemine dans la partie superieure du cortex pour ensuite se diriger vers la medullaire ou il devient &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;clic : l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;anse de henle qui descend dans la medullaire ou elle effectue un virage en epingle a cheveux pour remonter dans le cortex ou elle devient&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;clic : le tubule contourne distal &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;clic : les tubules distaux de plusieurs nephrons se jettent dans la derniere partie du tubule renal appelee tubule collecteur. ce dernier est charge d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;acheminer l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;urine vers la papille renale.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;pour info (&lt;/span&gt;&lt;span  fontweight='bold'&gt;et ces chiffres ne sont pas a retenir, je repete ces chiffres ne sont pas a retenir&lt;/span&gt;&lt;span &gt;) le corpuscule renal fait environ 200 microns de diametre tandis que les tubules ont environ 55 microns de diametre et 30 a 50 mm de long. lorsqu'&lt;/span&gt;&lt;span &gt;on sait que chaque rein contient plus d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;un million de nephrons, on evalue la longueur totale des tubules renaux des deux reins chez l'homme a environ 115 km.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
