SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;chaque nephron est etroitement associe a &lt;/span&gt;&lt;span  fontweight='bold'&gt;deux lits capillaires&lt;/span&gt;&lt;span &gt;: le &lt;/span&gt;&lt;span  fontweight='bold'&gt;glomerule&lt;/span&gt;&lt;span &gt; et le &lt;/span&gt;&lt;span  fontweight='bold'&gt;lit capillaire peritubulaire&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le &lt;/span&gt;&lt;span  fontweight='bold'&gt;glomerule &lt;/span&gt;&lt;span &gt;est un bouquet de capillaires specialises dans la &lt;/span&gt;&lt;span  fontweight='bold'&gt;filtration&lt;/span&gt;&lt;span &gt; et il presente une difference fondamentale avec les autres lits capillaires de l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;organisme car il est situe&lt;/span&gt;&lt;span  fontweight='bold'&gt; entre 2 arterioles&lt;/span&gt;&lt;span &gt; : ainsi le sang arrive aux capillaires glomerulaires par l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;arteriole &lt;/span&gt;&lt;span &gt;glomerulaire &lt;/span&gt;&lt;span  fontweight='bold'&gt;afferente, issue de l&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;artere interlobulaire, &lt;/span&gt;&lt;span &gt; et les quitte par l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;arteriole &lt;/span&gt;&lt;span &gt;glomerulaire &lt;/span&gt;&lt;span  fontweight='bold'&gt;efferente&lt;/span&gt;&lt;span &gt;. pour rappel, dans la plupart des tissus de l'organisme les capillaires recoivent le sang en provenance des arterioles et le deversent dans des veinules.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les capillaires peritubulaires quant a eux, sont issus de l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;arteriole efferente qui draine le glomerule. ces capillaires sont specialises dans la &lt;/span&gt;&lt;span  fontweight='bold'&gt;reabsorption&lt;/span&gt;&lt;span &gt;, ils entourent le tubule renal et se deversent dans le reseau veineux renal.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
