SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la &lt;/span&gt;&lt;span  fontweight='bold'&gt;filtration&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span  fontweight='bold'&gt;glomerulaire &lt;/span&gt;&lt;span &gt;s&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;effectue au niveau du corpuscule renal qui comprend les capillaires glomerulaires entoures par la capsule de bowman. il s&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;agit essentiellement d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;un processus unidirectionnel, passif et non selectif envers tous les solutes du plasma sauf les proteines. ces solutes et le liquide plasmatique sont &lt;/span&gt;&lt;span  fontweight='bold'&gt;pousses&lt;/span&gt;&lt;span &gt; a travers la membrane de filtration par la pression qui regne a l'interieur des capillaires glomerulaires. comme ce processus ne demande pas d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;energie, on peut considerer les glomerules comme de &lt;/span&gt;&lt;span  fontweight='bold'&gt;simples filtres mecaniques&lt;/span&gt;&lt;span &gt;. la filtration glomerulaire est un processus &lt;/span&gt;&lt;span  fontweight='bold'&gt;d&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;ultrafiltration&lt;/span&gt;&lt;span &gt; puisqu'elle se fait a travers une membrane &lt;/span&gt;&lt;span  fontweight='bold'&gt;semi-permeable &lt;/span&gt;&lt;span &gt;qui retient les globules sanguins, les plaquettes et les proteines, et laisse passer l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;eau et les substances dissoutes du plasma.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
