SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;avant d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;aborder le processus de filtration, nous allons d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;abord examiner la structure du corpuscule renal. la &lt;/span&gt;&lt;span  fontweight='bold'&gt;capsule &lt;/span&gt;&lt;span &gt;de bowman est formee de &lt;/span&gt;&lt;span  fontweight='bold'&gt;2 feuillets &lt;/span&gt;&lt;span &gt;separes par une cavite, l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;espace capsulaire&lt;/span&gt;&lt;span &gt;,&lt;/span&gt;&lt;span  fontweight='bold'&gt; &lt;/span&gt;&lt;span &gt;egalement appele &lt;/span&gt;&lt;span  fontweight='bold' fontstyle='italic'&gt;chambre de filtration&lt;/span&gt;&lt;span &gt;, qui se prolonge au niveau du pole urinaire par le tubule renal&lt;/span&gt;&lt;span  fontweight='bold'&gt;. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le &lt;/span&gt;&lt;span  fontweight='bold' fontstyle='italic'&gt;feuillet parietal  &lt;/span&gt;&lt;span  fontweight='bold'&gt;externe&lt;/span&gt;&lt;span &gt; de la capsule est compose d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;un epithelium simple squameux, c'est-a-dire constitue de cellules aplaties, qui a un role strictement &lt;/span&gt;&lt;span  fontweight='bold'&gt;structural&lt;/span&gt;&lt;span &gt;. il ne participe donc pas a la formation du filtrat. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le &lt;/span&gt;&lt;span  fontweight='bold' fontstyle='italic'&gt;feuillet visceral&lt;/span&gt;&lt;span &gt;  s&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;attache aux capillaires du glomerule et il est compose de cellules epitheliales modifiees et ramifiees appelees &lt;/span&gt;&lt;span  fontweight='bold'&gt;podocytes&lt;/span&gt;&lt;span &gt;. le corps cellulaire des podocytes emet de longs prolongements cytoplasmiques qui entourent les capillaires glomerulaires et s&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;entrelacent les uns avec les autres comme des doigts qui se croisent. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;a l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;oppose du pole urinaire, le sang arrive et repart du glomerule au niveau du pole &lt;/span&gt;&lt;span  fontweight='bold'&gt;vasculaire&lt;/span&gt;&lt;span &gt; ou se trouvent par les arterioles afferentes et efferentes.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
