SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les podocytes de l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;epithelium visceral ont un peu la forme de pieuvres. leurs prolongements cytoplasmiques se terminent en &lt;/span&gt;&lt;span  fontweight='bold'&gt;pedicelles&lt;/span&gt;&lt;span &gt;, des petites formations enchevetrees qui s&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;attachent a la &lt;/span&gt;&lt;span  fontweight='bold'&gt;lame basale des capillaires glomerulaires&lt;/span&gt;&lt;span &gt;. le filtrat s&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;echappant des capillaires glomerulaires passe a travers les &lt;/span&gt;&lt;span  fontweight='bold'&gt;fentes de filtration&lt;/span&gt;&lt;span &gt;, des espaces entre les pedicelles fermes par une fine membrane.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
