SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;en fait, pour sortir du plasma et atteindre l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;espace capsulaire les substances doivent franchir &lt;/span&gt;&lt;span  fontweight='bold'&gt;trois barrieres &lt;/span&gt;&lt;span &gt;qui constituent &lt;/span&gt;&lt;span  fontweight='bold'&gt;la membrane de filtration&lt;/span&gt;&lt;span &gt;.  &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;abord, elles passent par les pores presents dans l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;endothelium des capillaires glomerulaires, qu'on qualifie ainsi de &lt;/span&gt;&lt;span  fontweight='bold'&gt;capillaires fenestres&lt;/span&gt;&lt;span &gt;. puis par la &lt;/span&gt;&lt;span  fontweight='bold'&gt;membrane basale &lt;/span&gt;&lt;span &gt;constituee par la fusion des lames basales de l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;endothelium capillaire et de l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;epithelium visceral capsulaire. la derniere barriere a franchir est la membrane qui ferme les fentes de filtration de podocytes, que l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;on appelle egalement &lt;/span&gt;&lt;span  fontweight='bold'&gt;diaphragmes&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
