SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;les &lt;/span&gt;&lt;span  fontweight='bold'&gt;trois filtres en serie &lt;/span&gt;&lt;span &gt;qui composent cette membrane de filtration ont chacun des proprietes particulieres qui leur permettent de bloquer la filtration des elements figures du sang et des proteines plasmatiques et de &lt;/span&gt;&lt;span  fontweight='bold'&gt;laisser passer l'eau et tous les autres solutes&lt;/span&gt;&lt;span &gt;. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;d'abord, les &lt;/span&gt;&lt;span  fontweight='bold'&gt;pores des capillaires glomerulaires &lt;/span&gt;&lt;span &gt;retiennent les cellules sanguines et les macromolecules de poids moleculaire superieur a 68000 daltons.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;puis, la &lt;/span&gt;&lt;span  fontweight='bold'&gt;membrane basale, &lt;/span&gt;&lt;span &gt;faite d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;un reseau de glycoproteines et de fibres de collagene, qui bloque le passage de la plupart des &lt;/span&gt;&lt;span  fontweight='bold'&gt;proteines plasmatiques dont l'albumine (d'un poids moleculaire de 65000 dalton). &lt;/span&gt;&lt;span &gt;ce sont tout particulierement les &lt;/span&gt;&lt;span  fontweight='bold'&gt;glycoproteines anioniques &lt;/span&gt;&lt;span &gt;de la membrane qui refoulent ces proteines puisque celles-ci, comme l'albumine, sont egalement chargees negativement.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;finalement, les &lt;/span&gt;&lt;span  fontweight='bold'&gt;toutes&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span  fontweight='bold'&gt;petites proteines &lt;/span&gt;&lt;span &gt;qui reussissent a traverser la membrane basale sont bloquees par les &lt;/span&gt;&lt;span  fontweight='bold' fontstyle='italic'&gt;diaphragmes &lt;/span&gt;&lt;span &gt;des fentes de filtration. elles sont ensuite englobees par les podocytes puis degradees.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
