SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le filtrat glomerulaire que l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;on retrouve dans la capsule de bowman est donc simplement un ultrafiltrat du sang sans ses globules, ses plaquettes ses proteines. les quelques proteines qui arriveraient eventuellement a franchir la membrane de filtration sont en plus eliminees du filtrat par les cellules du tubule renal qui les recuperent par endocytose.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la composition et l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;osmolarite de 300 mosmoles par litre du filtrat sont presque identiques a celle du plasma sauf pour les substances liees aux proteines plasmatiques qui ne peuvent pas passer la membrane de filtration.  &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;c&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;est le cas du calcium (qui est lie a 40%), des acides gras, des hormones steroidiennes et de certains medicaments.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
