SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la&lt;/span&gt;&lt;span  fontweight='bold'&gt; pression hydrostatique glomerulaire&lt;/span&gt;&lt;span &gt; est en moyenne de &lt;/span&gt;&lt;span  fontweight='bold'&gt;55 mmhg &lt;/span&gt;&lt;span &gt;et c&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;est la principale force qui pousse le filtrat &lt;/span&gt;&lt;span  fontweight='bold'&gt;hors&lt;/span&gt;&lt;span &gt; du glomerule. la &lt;/span&gt;&lt;span  fontweight='bold'&gt;pression hydrostatique capsulaire &lt;/span&gt;&lt;span &gt;(de 15 mmhg) s&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;y oppose de meme que la &lt;/span&gt;&lt;span  fontweight='bold'&gt;pression oncotique glomerulaire&lt;/span&gt;&lt;span &gt; (de 30 mmhg) qui tend a faire rentrer l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;eau dans les capillaires glomerulaires.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;la pnf a l&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;origine du debit de filtration de 125 ml/min est donc de 10 mmhg. &lt;/span&gt;&lt;span &gt;ceci peu sembler faible mais comme elle s&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;exerce a travers une membrane tres permeable, elle entraine une filtration rapide et abondante.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;le debit de filtration glomerulaire est egalement influence par deux facteurs : la pression nette de filtration mais egalement par le &lt;/span&gt;&lt;span  fontweight='bold'&gt;coefficient d&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;ultrafiltration kf &lt;/span&gt;&lt;span &gt;de la membrane de filtration qui tient compte de la &lt;/span&gt;&lt;span  fontweight='bold'&gt;permeabilite&lt;/span&gt;&lt;span &gt; et de la &lt;/span&gt;&lt;span  fontweight='bold'&gt;surface totale des capillaires disponibles pour la filtration&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
