SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le coefficient d'ultrafiltration (&lt;/span&gt;&lt;span  fontweight='bold'&gt;kf&lt;/span&gt;&lt;span &gt;) est donc le produit de la &lt;/span&gt;&lt;span  fontweight='bold'&gt;surface de filtration &lt;/span&gt;&lt;span &gt;des capillaires glomerulaires et de la &lt;/span&gt;&lt;span  fontweight='bold'&gt;permeabilite&lt;/span&gt;&lt;span &gt; de la paroi capillaire. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la &lt;/span&gt;&lt;span  fontweight='bold'&gt;surface de filtration &lt;/span&gt;&lt;span &gt;est regulee par les &lt;/span&gt;&lt;span  fontweight='bold'&gt;cellules mesangiales &lt;/span&gt;&lt;span &gt;glomerulaires. ce sont des cellules etoilees disposees &lt;/span&gt;&lt;span  fontweight='bold'&gt;entre&lt;/span&gt;&lt;span &gt; les capillaires glomerulaires qui contiennent des faisceaux de &lt;/span&gt;&lt;span  fontweight='bold'&gt;filaments contractiles&lt;/span&gt;&lt;span &gt;. en presence de certaines substances, elle peuvent se &lt;/span&gt;&lt;span  fontweight='bold'&gt;contracter&lt;/span&gt;&lt;span &gt; et diminuer le dfg en &lt;/span&gt;&lt;span  fontweight='bold'&gt;reduisant la surface totale des capillaires actifs &lt;/span&gt;&lt;span &gt;et donc le &lt;/span&gt;&lt;span  fontweight='bold'&gt;coefficient de filtration&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;toutefois, en conditions physiologiques normales, leur role est negligeable et le coefficient de filtration membranaire varie peu. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
