SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;afin de mieux comprendre comment s&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;effectue le controle du debit de filtration glomerulaire par les arterioles, il faut d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;abord aborder quelques mecanismes que vous verrez plus en detail dans le cours de physiologie de la circulation. de facon generale, toutes les arterioles de notre organisme ont la capacite de se contracter et de se relacher grace a leur paroi riche en &lt;/span&gt;&lt;span  fontweight='bold'&gt;fibres musculaires lisses innervees par le systeme nerveux sympathique&lt;/span&gt;&lt;span &gt;. rappelez-vous egalement la notion de resistance a l'ecoulement que nous avons vue au chapitre 3 lorsque nous avons regarder les caracteristiques hemodynamiques de la circulation du nephron.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;come vous le voyez dans le petit schema en bas a droite, le diametre de ces vaisseaux est &lt;/span&gt;&lt;span  fontweight='bold'&gt;reduit&lt;/span&gt;&lt;span &gt; par la &lt;/span&gt;&lt;span  fontweight='bold'&gt;vasoconstriction&lt;/span&gt;&lt;span &gt; et &lt;/span&gt;&lt;span  fontweight='bold'&gt;augmente&lt;/span&gt;&lt;span &gt; par la &lt;/span&gt;&lt;span  fontweight='bold'&gt;vasodilatation&lt;/span&gt;&lt;span &gt;. par consequent, une &lt;/span&gt;&lt;span  fontweight='bold'&gt;vasoconstriction arteriolaire et donc &lt;/span&gt;&lt;span &gt;une &lt;/span&gt;&lt;span  fontweight='bold'&gt;diminution de diametre&lt;/span&gt;&lt;span &gt; se traduira par une &lt;/span&gt;&lt;span  fontweight='bold'&gt;augmentation de la resistance a l&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;ecoulement &lt;/span&gt;&lt;span &gt;et donc par une accumulation de sang et une &lt;/span&gt;&lt;span  fontweight='bold'&gt;augmentation de pression en amont &lt;/span&gt;&lt;span &gt;et une &lt;/span&gt;&lt;span  fontweight='bold'&gt;diminution de debit &lt;/span&gt;&lt;span &gt;et donc &lt;/span&gt;&lt;span  fontweight='bold'&gt;de pression en aval&lt;/span&gt;&lt;span &gt; du site de constriction. une &lt;/span&gt;&lt;span  fontweight='bold'&gt;vasodilatation&lt;/span&gt;&lt;span &gt; aura bien sur &lt;/span&gt;&lt;span  fontweight='bold'&gt;les effets inverses&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
