SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;autrement dit, lorsque la resistance des arterioles renales augmente, le debit sanguin renal (fsr sur ce schema pour flux sanguin renal)&lt;/span&gt;&lt;span  fontweight='bold'&gt;, situe en aval&lt;/span&gt;&lt;span &gt;, &lt;/span&gt;&lt;span  fontweight='bold'&gt;diminue&lt;/span&gt;&lt;span &gt;. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span  fontweight='bold'&gt;l&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;effet sur le debit de filtration glomerulaire &lt;/span&gt;&lt;span &gt;dependra cependant &lt;/span&gt;&lt;span  fontweight='bold'&gt;du lieu ou se fera l&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;augmentation de resistance&lt;/span&gt;&lt;span &gt;. ceci est du a l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;organisation particuliere de la circulation glomerulaire avec ses deux arterioles afferente et efferente. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;sur cette diapositive, le schema de gauche represente le flux c&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;est-a-dire le debit sanguin renal et le debit de filtration glomerulaire en dehors de tout changement de rayon des arterioles. nous voyons a droite que &lt;/span&gt;&lt;span  fontweight='bold'&gt;lors d&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;une vasoconstriction de l&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;arteriole afferente&lt;/span&gt;&lt;span &gt;, l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;augmentation de resistance se traduit par une diminution de la pression en aval donc de la &lt;/span&gt;&lt;span  fontweight='bold'&gt;pression hydrostatique glomerulaire et de la pnf&lt;/span&gt;&lt;span &gt;. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une vasoconstriction de l'arteriole afferente entrainera donc une &lt;/span&gt;&lt;span  fontweight='bold'&gt;diminution du debit sanguin renal &lt;/span&gt;&lt;span  fontweight='bold' textdecoration='underline'&gt;et&lt;/span&gt;&lt;span  fontweight='bold'&gt; du debit de filtration glomerulaire&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;effet inverse sera observe avec une vasodilatation, et donc une diminution, de la resistance de l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;arteriole afferente.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
