SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le mecanisme vasculaire myogene n&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;est pas observe uniquement dans les arterioles afferentes renales. il reflete la tendance generale du muscle lisse vasculaire a se contracter sous l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;effet de l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;etirement. c&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;est ainsi que &lt;/span&gt;&lt;span  fontweight='bold'&gt;l&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;etirement de la paroi des arterioles afferentes &lt;/span&gt;&lt;span &gt;par une &lt;/span&gt;&lt;span  fontweight='bold'&gt;augmentation&lt;/span&gt;&lt;span &gt; de pression sanguine entraine une ouverture &lt;/span&gt;&lt;span  fontweight='bold'&gt;de canaux calciques sensibles a l&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;etirement,  une entree de calcium dans les cellules musculaires lisses et leur contraction&lt;/span&gt;&lt;span &gt;. la vasoconstriction provoque une &lt;/span&gt;&lt;span  fontweight='bold'&gt;diminution du debit de filtration glomerulaire &lt;/span&gt;&lt;span &gt;limitant ainsi l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;effet indesirable de la pression sur le debit. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une diminution de la pression arterielle a l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;effet inverse, provoquant une relaxation du muscle lisse arteriolaire.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
