SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;contrairement au mecanisme vasculaire myogene, le mecanisme de retrocontrole tubulo-gomerulaire est &lt;/span&gt;&lt;span  fontweight='bold'&gt;propre au rein&lt;/span&gt;&lt;span &gt;. c&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;est une &lt;/span&gt;&lt;span  fontweight='bold'&gt;boucle de controle locale &lt;/span&gt;&lt;span &gt;impliquant l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;appareil juxta-glomerulaire&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on parle de &lt;/span&gt;&lt;span  fontweight='bold' textdecoration='underline'&gt;retro&lt;/span&gt;&lt;span  fontweight='bold'&gt;controle&lt;/span&gt;&lt;span &gt; car, dans ce mecanisme, la &lt;/span&gt;&lt;span  fontweight='bold'&gt;filtration glomerulaire &lt;/span&gt;&lt;span &gt;est regulee par les &lt;/span&gt;&lt;span  fontweight='bold'&gt;cellules&lt;/span&gt;&lt;span &gt; de la &lt;/span&gt;&lt;span  fontweight='bold'&gt;macula densa &lt;/span&gt;&lt;span &gt;de l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;appareil juxta-glomerulaire qui sont situees dans le &lt;/span&gt;&lt;span  fontweight='bold'&gt;tubule renal distal &lt;/span&gt;&lt;span &gt;donc bien en aval du lieu de la filtration. ces cellules sont de veritables &lt;/span&gt;&lt;span  fontweight='bold'&gt;osmorecepteurs&lt;/span&gt;&lt;span &gt; sensibles a la composition du liquide tubulaire. en reponse a des &lt;/span&gt;&lt;span  fontweight='bold'&gt;variations de la concentration tubulaire de nacl, &lt;/span&gt;&lt;span &gt;les cellules de la macula densa liberent une substance qui va agir sur &lt;/span&gt;&lt;span  fontweight='bold'&gt;les cellules musculaires lisses de l&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;arteriole afferente&lt;/span&gt;&lt;span &gt;. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;on parle dans ce cas de regulation &lt;/span&gt;&lt;span  fontweight='bold'&gt;paracrine&lt;/span&gt;&lt;span &gt;, car les effets de la substance liberee s'exercent &lt;/span&gt;&lt;span  fontweight='bold'&gt;a&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span  fontweight='bold'&gt;proximite&lt;/span&gt;&lt;span &gt;, par opposition a la regulation endocrine, comme celle de plusieurs hormones, qui agissent a distance de leur site de liberation.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
