SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le mecanisme tubulo-glomerulaire fonctionne de la facon suivante.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une &lt;/span&gt;&lt;span  fontweight='bold'&gt;augmentation&lt;/span&gt;&lt;span &gt; du dfg entraine une &lt;/span&gt;&lt;span  fontweight='bold'&gt;augmentation de la concentration tubulaire de nacl &lt;/span&gt;&lt;span &gt;(parce que sa &lt;/span&gt;&lt;span  fontweight='bold'&gt;filtration&lt;/span&gt;&lt;span &gt; est augmentee mais egalement parce que l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;augmentation de la vitesse d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;ecoulement du filtrat dans les tubules &lt;/span&gt;&lt;span  fontweight='bold'&gt;limite la capacite de reabsorption tubulaire&lt;/span&gt;&lt;span &gt;). en reponse a l'elevation de la concentration en nacl du liquide tubulaire, les cellules de la macula densa vont liberer une substance vasoconstrictrice qui va provoquer la &lt;/span&gt;&lt;span  fontweight='bold'&gt;contraction de l&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;arteriole afferente &lt;/span&gt;&lt;span &gt;et &lt;/span&gt;&lt;span  fontweight='bold'&gt;diminuer le dfg&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;une &lt;/span&gt;&lt;span  fontweight='bold'&gt;diminution&lt;/span&gt;&lt;span &gt; du dfg aura les effets &lt;/span&gt;&lt;span  fontweight='bold'&gt;inverses&lt;/span&gt;&lt;span &gt; sur la concentration tubulaire de nacl. les cellules de la macula libereront &lt;/span&gt;&lt;span  fontweight='bold'&gt;moins de substance vasoconstrictrice&lt;/span&gt;&lt;span &gt;. la consequence sera une &lt;/span&gt;&lt;span  fontweight='bold'&gt;vasodilatation&lt;/span&gt;&lt;span &gt; de l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;arteriole afferente et une &lt;/span&gt;&lt;span  fontweight='bold'&gt;augmentation du dfg&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la signalisation paracrine entre la macula densa et l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;arteriole afferente est complexe et l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;on ne connait toujours pas la (ou les) substances impliquees. plusieurs mediateurs ont ete proposes comme l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;adenosine, le monoxyde d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;azote, les prostaglandines ou encore l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;endotheline.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
