SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;activation du systeme renine-angiotensine commence avec la secretion de renine par les cellules de l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;appareil juxtaglomerulaire.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;la renine est une &lt;/span&gt;&lt;span  fontweight='bold'&gt;enzyme&lt;/span&gt;&lt;span &gt; dont le substrat est &lt;/span&gt;&lt;span  fontweight='bold'&gt;l&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;angiotensinogene&lt;/span&gt;&lt;span &gt;, une proteine plasmatique inactive produite par le foie. sous l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;action de la renine, l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;angiotensinogene est converti dans la circulation sanguine en un decapeptide, &lt;/span&gt;&lt;span  fontweight='bold'&gt;l&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;angiotensine i&lt;/span&gt;&lt;span &gt;, egalement inactive. puis l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;angiotensine i est a son tour convertie en &lt;/span&gt;&lt;span  fontweight='bold'&gt;angiotensine ii&lt;/span&gt;&lt;span &gt; sous l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;effet d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;une enzyme appelee &lt;/span&gt;&lt;span  fontweight='bold'&gt;enzyme de conversion de l&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;angiotensine&lt;/span&gt;&lt;span &gt;, que l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;on trouve dans l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;endothelium capillaire des poumons mais egalement dans d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;autres organes dont le rein. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;angiotensine ii est un tout petit peptide de 8 acides amines qui a un role &lt;/span&gt;&lt;span  fontweight='bold'&gt;fondamental&lt;/span&gt;&lt;span &gt; dans la regulation de la pression arterielle. elle a de multiples actions &lt;/span&gt;&lt;span  fontweight='bold'&gt;toutes destinees a augmenter la pression arterielle&lt;/span&gt;&lt;span &gt;. parmi celles-ci, nous citerons &lt;/span&gt;&lt;span  fontweight='bold'&gt;un effet vasoconstricteur puissant&lt;/span&gt;&lt;span &gt;, en particulier au niveau des &lt;/span&gt;&lt;span  fontweight='bold'&gt;arterioles&lt;/span&gt;&lt;span &gt;, et une &lt;/span&gt;&lt;span  fontweight='bold'&gt;stimulation de la synthese et de la liberation d&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;aldosterone par les surrenales&lt;/span&gt;&lt;span &gt;. comme nous le verrons au chapitre 8, l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;aldosterone augmente le volume du liquide extracellulaire, et donc le &lt;/span&gt;&lt;span  fontweight='bold'&gt;volume plasmatique&lt;/span&gt;&lt;span &gt;, en favorisant la reabsorption de sodium et d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;eau par le rein.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
