SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;d'un point de vue &lt;/span&gt;&lt;span  fontweight='bold'&gt;fonctionnel&lt;/span&gt;&lt;span &gt; mais egalement &lt;/span&gt;&lt;span  fontweight='bold'&gt;histologique&lt;/span&gt;&lt;span &gt;, les parois du tubule contourne distal et du tubule collecteur se ressemblent. elles comportent &lt;/span&gt;&lt;span  fontweight='bold'&gt;deux types de cellules&lt;/span&gt;&lt;span &gt;. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;abord les &lt;/span&gt;&lt;span  fontweight='bold'&gt;cellules principales &lt;/span&gt;&lt;span &gt;qui &lt;/span&gt;&lt;span  fontweight='bold'&gt;reabsorbent&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span  fontweight='bold'&gt;du sodium et secretent du potassium&lt;/span&gt;&lt;span &gt; et nous verrons que cette fonction est regulee par une hormone, l'aldosterone. la permeabilite a &lt;/span&gt;&lt;span  fontweight='bold'&gt;l&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;eau&lt;/span&gt;&lt;span &gt; de ces cellules est egalement sous le controle d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;une hormone, l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;hormone antidiuretique ou adh. ainsi, &lt;/span&gt;&lt;span  fontweight='bold'&gt;en presence d&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;adh, les cellules principales reabsorbent de l&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;eau&lt;/span&gt;&lt;span &gt;. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les &lt;/span&gt;&lt;span  fontweight='bold'&gt;cellules intercalaires&lt;/span&gt;&lt;span &gt;, quant a elles,&lt;/span&gt;&lt;span  fontweight='bold'&gt; &lt;/span&gt;&lt;span &gt;sont impliquees de facon majoritaire dans la &lt;/span&gt;&lt;span  fontweight='bold'&gt;regulation de l&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;equilibre acido-basique&lt;/span&gt;&lt;span &gt;. selon les besoins de l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;organisme elles ont la capacite de &lt;/span&gt;&lt;span  fontweight='bold'&gt;reabsorber des ions bicarbonates &lt;/span&gt;&lt;span &gt;et de &lt;/span&gt;&lt;span  fontweight='bold'&gt;secreter&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span  fontweight='bold'&gt;des protons&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
