SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;la regulation de la l'equilibre hydrique de notre organisme est tres precise puisque notre osmolarite plasmatique varie tres peu entre 280 a 285 mosmoles par litre. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;ceci est possible grace a trois mecanismes essentiels qui sont :&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;- la secretion d&lt;/span&gt;&lt;span  fontweight='bold'&gt;'hormone antidiuretique ou adh, aussi appelee vasopressine&lt;/span&gt;&lt;span &gt;, capable de modifier la permeabilite du tubule renal a l'eau;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;un role majeur du rein qui permet de &lt;/span&gt;&lt;span  fontweight='bold'&gt;diluer&lt;/span&gt;&lt;span &gt; ou de &lt;/span&gt;&lt;span  fontweight='bold'&gt;concentrer&lt;/span&gt;&lt;span &gt; l'urine selon les besoins;&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;et le &lt;/span&gt;&lt;span  fontweight='bold'&gt;reflexe de la soif&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
