SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'adh est en fait une &lt;/span&gt;&lt;span  fontweight='bold'&gt;neurohormone &lt;/span&gt;&lt;span &gt;car elle est produite par des cellules nerveuses situees dans une structure du cerveau dont vous avez peut-etre deja entendu parler, &lt;/span&gt;&lt;span  fontweight='bold'&gt;l'hypothalamus&lt;/span&gt;&lt;span &gt;. c'est un centre de controle majeur qui intervient dans la regulation de nombreux parametres physiologiques.  &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;les neurones hypothalamiques projettent leurs terminaisons axonales jusqu'au niveau de &lt;/span&gt;&lt;span  fontweight='bold'&gt;l'hypophyse&lt;/span&gt;&lt;span &gt;, une toute petite glande de la taille d'un petit pois situee a la base du cerveau juste en dessous de l'hypothalamus. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;plus precisement, les neurones secreteur d'adh se projettent dans la &lt;/span&gt;&lt;span  fontweight='bold'&gt;partie posterieure de l'hypophyse&lt;/span&gt;&lt;span &gt;, egalement appelee &lt;/span&gt;&lt;span  fontweight='bold'&gt;neurohypophyse&lt;/span&gt;&lt;span &gt;. l'adh est d'abord synthetisee au niveau de l'hypothalamus puis elle est emmagasinee dans des granules de secretion qui se &lt;/span&gt;&lt;span  fontweight='bold'&gt;deplacent le long des axones&lt;/span&gt;&lt;span &gt; jusqu'aux terminaisons nerveuses situees dans l'hypophyse posterieure. elle est &lt;/span&gt;&lt;span  fontweight='bold'&gt;liberee dans la circulation sanguine de l'hypophyse posterieure &lt;/span&gt;&lt;span &gt;en reponse a la &lt;/span&gt;&lt;span  fontweight='bold'&gt;stimulation des neurones hypothalamiques&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
