SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;l'hormone antidiuretique agit sur les &lt;/span&gt;&lt;span  fontweight='bold'&gt;cellules principales du tcd et du tubule collecteur &lt;/span&gt;&lt;span &gt;pour modifier leur &lt;/span&gt;&lt;span  fontweight='bold'&gt;permeabilite membranaire a l'eau&lt;/span&gt;&lt;span &gt;. les cellules principales ont la propriete de n'&lt;/span&gt;&lt;span &gt;etre permeables a l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;eau &lt;/span&gt;&lt;span  fontweight='bold'&gt;qu&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;en presence d&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;adh&lt;/span&gt;&lt;span &gt;. en effet leur membrane apicale ne possede &lt;/span&gt;&lt;span  fontweight='bold'&gt;qu&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;un seul type d&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;aquaporine, l&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;aquaporine 2,&lt;/span&gt;&lt;span &gt; et la quantite membranaire de ces aquaporines &lt;/span&gt;&lt;span  fontweight='bold'&gt;est regulee par l&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;adh&lt;/span&gt;&lt;span &gt;. ainsi, en l&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;absence d&lt;/span&gt;&lt;span &gt;'&lt;/span&gt;&lt;span &gt;adh, les aquaporines 2 sont stockees dans des vesicules cytoplasmiques (comme indique &lt;/span&gt;&lt;span  fontweight='bold'&gt;en 3&lt;/span&gt;&lt;span &gt; sur cette figure).&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt; nous voyons &lt;/span&gt;&lt;span  fontweight='bold'&gt;en 1&lt;/span&gt;&lt;span &gt; que la fixation de l'&lt;/span&gt;&lt;span  fontweight='bold'&gt;adh &lt;/span&gt;&lt;span &gt;sur son &lt;/span&gt;&lt;span  fontweight='bold'&gt;recepteur v2 &lt;/span&gt;&lt;span &gt;sur&lt;/span&gt;&lt;span  fontweight='bold'&gt; &lt;/span&gt;&lt;span &gt;la membrane &lt;/span&gt;&lt;span  fontweight='bold'&gt;basale&lt;/span&gt;&lt;span &gt; du tubule collecteur active une cascade de signalisation a travers une &lt;/span&gt;&lt;span  fontweight='bold'&gt;proteine g stimulatrice &lt;/span&gt;&lt;span &gt;couplee a &lt;/span&gt;&lt;span  fontweight='bold'&gt;l&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;adenylate cyclase&lt;/span&gt;&lt;span &gt;. &lt;/span&gt;&lt;span  fontweight='bold'&gt;la production d&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;ampc &lt;/span&gt;&lt;span &gt;et &lt;/span&gt;&lt;span  fontweight='bold'&gt;l&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;activation de la proteine kinase a &lt;/span&gt;&lt;span &gt;declenchent alors la migration des vesicules de stockage des aquaporines 2 vers la membrane apicale avec laquelle elles fusionnent. cette &lt;/span&gt;&lt;span  fontweight='bold'&gt;exocytose&lt;/span&gt;&lt;span &gt; permet &lt;/span&gt;&lt;span  fontweight='bold'&gt;l&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;insertion&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span  fontweight='bold'&gt;des aquaporines 2 dans la membrane &lt;/span&gt;&lt;span &gt;et &lt;/span&gt;&lt;span  fontweight='bold'&gt;l&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;augmentation&lt;/span&gt;&lt;span &gt; de la &lt;/span&gt;&lt;span  fontweight='bold'&gt;permeabilite&lt;/span&gt;&lt;span &gt;  a &lt;/span&gt;&lt;span  fontweight='bold'&gt;l&lt;/span&gt;&lt;span  fontweight='bold'&gt;'&lt;/span&gt;&lt;span  fontweight='bold'&gt;eau&lt;/span&gt;&lt;span &gt; des tubules distal et collecteur.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
