SDpagenotes[SDpagenum] = "&lt;textflow xmlns = 'http://ns.adobe.com/textlayout/2008' fontfamily = 'verdana' fontsize = '9'&gt;&lt;p&gt;&lt;span &gt;le principal stimulus de la secretion d'adh est une &lt;/span&gt;&lt;span  fontweight='bold'&gt;augmentation de l'osmolarite du liquide extracellulaire qui devient hypertonique&lt;/span&gt;&lt;span &gt;. les changements &lt;/span&gt;&lt;span  fontweight='bold'&gt;de volume et d'osmolarite intracellulaire qui en decoulent activent des osmorecepteurs&lt;/span&gt;&lt;span &gt;, situes dans &lt;/span&gt;&lt;span  fontweight='bold'&gt;l'hypothalamus au voisinage des neurones secreteurs d'adh&lt;/span&gt;&lt;span &gt;. les osmorecepteurs &lt;/span&gt;&lt;span  fontweight='bold'&gt;stimulent les neurones a adh provoquant sa liberation par l'hypophyse posterieure&lt;/span&gt;&lt;span &gt;.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;l'augmentation de la permeabilite a l'eau du segment terminal du nephron par l'adh &lt;/span&gt;&lt;span  fontweight='bold'&gt;favorise la&lt;/span&gt;&lt;span &gt; &lt;/span&gt;&lt;span  fontweight='bold'&gt;reabsorption d'eau &lt;/span&gt;&lt;span &gt;et &lt;/span&gt;&lt;span  fontweight='bold'&gt;diminue son excretion&lt;/span&gt;&lt;span &gt;. le volume urinaire diminue et la concentration de l'urine augmente.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;lorsque la reabsorption d'eau est suffisante pour retablir l'osmolarite plasmatique, la secretion d'adh s'interrompt.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;bien evidemment, l'inverse se produit lorsque le liquide extracellulaire devient &lt;/span&gt;&lt;span  fontweight='bold'&gt;hypotonique&lt;/span&gt;&lt;span &gt;, par exemple en cas d'ingestion massive d'eau. &lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;span &gt;cette regulation reflexe de la secretion d'adh est un bon exemple de boucle de &lt;/span&gt;&lt;span  fontweight='bold'&gt;retroaction negative &lt;/span&gt;&lt;span &gt;tel qu'il en existe a plusieurs niveaux dans notre organisme.&lt;/span&gt;&lt;span &gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;/textflow&gt;";
